// This function is duplicated in the app.pub.js file
// Move to a shared file
function fetchRecordCount(base_url,element,is_class,db_table,p1,c1,p2,c2,p3,c3) {

    // db_table is the name of the database table
    // p vars are column names, c vars are their associated conditions
    // Used in conjunction in WHERE statements
    // Example: 
    // -- If db_table var is "brewing", the p1 var is "brewBrewerID", and the c1 var is "3"
    // -- The php code will execute SELECT COUNT(*) as 'count' FROM brewing WHERE brewBrewerID = '3';

    if (is_class == 1) {
        prefix = ".";
    }
    else {
        prefix = "#";
    }

    element = prefix + element;
    element_updated = element + "-updated";

    // console.log(element_updated);

    // Build AJAX URL
    ajax_url = base_url + "ajax/count_records.ajax.php?section=" + db_table;
    if ((typeof p1 !== 'undefined') && (typeof c1 !== 'undefined')) ajax_url = ajax_url + "&p1=" + p1 + "&c1=" + c1;
    if ((typeof p2 !== 'undefined') && (typeof c2 !== 'undefined')) ajax_url = ajax_url + "&p2=" + p2 + "&c2=" + c2;
    if ((typeof p3 !== 'undefined') && (typeof c3 !== 'undefined')) ajax_url = ajax_url + "&p3=" + p3 + "&c3=" + c3;

    // console.log(ajax_url);
    
    $.ajax({
        
        url: ajax_url,
        type: 'GET',
        dataType: 'json',
        
        success: function(response) {
            if (response.success) {
                $(element).html(response.count);
                $(element_updated).html(response.updated);
                console.log(response.count);
            } else {
                $(element).text('Error');
                $(element_updated).text('Failed to fetch data: ' + response.message);
            }
        },
        
        error: function(xhr, status, error) {
            $(element).text('Error');
            $(element_updated).text('Connection failed: ' + error);
        }
    
    });
    
}

/**
 * Admin: Tables Planning Mode
 * Function with an ajax call to convert 
 * all records in the judging_assignments 
 * and judging_flights tables to 1 (planning).
 */

function enable_planning_mode(ajax_url,admin_function) {
    
    jQuery.ajax({
        
        url: ajax_url+"tables_mode.ajax.php?section=enable-planning",

        success:function(data) {
            
            var jsonData = JSON.parse(data);
            console.log(jsonData.status);
            
            if (jsonData.status === "1") {

                $("#competition-mode-status").hide();
                $("#competition-mode-status-icon").attr("class", "small text-success");
                $("#competition-mode-status-icon").attr("class", "fa fa-check-circle text-success");
                $("#competition-mode-status").show().delay(5000).hide("fast");
                
                if (admin_function == "judging_tables") {

                    $("#competition-mode-status-text").html("Tables Planning Mode loaded successfully. Refreshing the page...");
                    $('#loader-submit').show(0).delay(30000).hide(0);
                    
                    // Refresh to get updated numbers
                    window.location.reload(true);
                
                }
                
            }
            
            else if (jsonData.status === "2") {

                $("#competition-mode-status").hide();
                $("#competition-mode-status-text").html("Tables Planning Mode was not loaded successfully.");
                $("#competition-mode-status-icon").attr("class", "small text-danger");
                $("#competition-mode-status-icon").attr("class", "fa fa-exclamation-circle text-warning");
                $("#competition-mode-status").show().delay(5000).hide("fast");

            }

            else {

                $("#competition-mode-status").hide();
                $("#competition-mode-status-text").html("There was an error.");
                $("#competition-mode-status-icon").attr("class", "small text-danger");
                $("#competition-mode-status-icon").attr("class", "fa fa-exclamation-circle text-warning");                
                $("#competition-mode-status").show().delay(5000).hide("fast");
            
            }
        
        },

        error:function() {
            console.log('Error');
        }

    });

}

