  array('brewStyleGroup' => '04','brewStyleNum' => '02','brewStyle' => 'Blonde Ale [BJCP 18A]','brewStyleCategory' => 'Pale Ale','brewStyleVersion' => 'AABC','brewStyleOG' => '1.038','brewStyleOGMax' => '1.054','brewStyleFG' => '1.008','brewStyleFGMax' => '1.013','brewStyleABV' => '3.8','brewStyleABVMax' => '5.5','brewStyleIBU' => '15','brewStyleIBUMax' => '28','brewStyleSRM' => '3','brewStyleSRMMax' => '6','brewStyleType' => '1','brewStyleInfo' => 'Easy-drinking, approachable, malt-oriented American craft beer, often with interesting fruit, hop, or character malt notes. Well-balanced and clean, is a refreshing pint without aggressive flavors.','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, pale-color, any-fermentation, north-america, craft-style, pale-ale-family, balanced','brewStyleComEx' => 'Kona Big Wave, Pelican Kiwanda Cream Ale, Victory Summer Love, Russian River Aud Blonde, Widmer Blonde Ale.','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '04','brewStyleNum' => '03','brewStyle' => 'Kolsch [BJCP 5B]','brewStyleCategory' => 'Pale Ale','brewStyleVersion' => 'AABC','brewStyleOG' => '1.044','brewStyleOGMax' => '1.05','brewStyleFG' => '1.007','brewStyleFGMax' => '1.011','brewStyleABV' => '4.4','brewStyleABVMax' => '5.2','brewStyleIBU' => '18','brewStyleIBUMax' => '30','brewStyleSRM' => '3.5','brewStyleSRMMax' => '5','brewStyleType' => '1','brewStyleInfo' => 'A clean, crisp, delicately-balanced beer usually with a very subtle fruit and hop character. Subdued maltiness throughout leads into a pleasantly well-attenuated and refreshing finish. Freshness makes a huge difference with this beer, as the delicate character can fade quickly with age. ','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, pale-color, top-fermented, lagered, central-europe, traditional-style, pale-ale-family, balanced','brewStyleComEx' => 'Fruh Kolsch, Reissdorf Kolsch, Gaffel Kolsch, Sunner Kolsch, Muhlen Kolsch, Sion Kolsch.','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '04','brewStyleNum' => '04','brewStyle' => 'Belgian Pale Ale [BJCP 24B]','brewStyleCategory' => 'Pale Ale','brewStyleVersion' => 'AABC','brewStyleOG' => '1.048','brewStyleOGMax' => '1.055','brewStyleFG' => '1.01','brewStyleFGMax' => '1.014','brewStyleABV' => '4.8','brewStyleABVMax' => '5.5','brewStyleIBU' => '20','brewStyleIBUMax' => '30','brewStyleSRM' => '8','brewStyleSRMMax' => '14','brewStyleType' => '1','brewStyleInfo' => 'A moderately malty, somewhat fruity-spicy, easy-drinking, copper-colored Belgian ale that is somewhat less aggressive in flavor profile than many other Belgian beers. The malt character tends to be a bit biscuity with light toasty, honey-like, or caramelly components; the fruit character is noticeable and complementary to the malt. The bitterness level is generally moderate, but may not seem as high due to the flavorful malt profile.','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, amber-color, top-fermented, western-europe, traditional-style, pale-ale-family, balanced','brewStyleComEx' => 'De Koninck, Speciale Palm, Dobble Palm, Russian River Perdition, Ginder Ale, Op-Ale, St. Pieters Zinnebir, Brewers Art House Pale Ale, Avery Karma, Eisenbahn Pale Ale, Blue Moon Pale Moon.','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '04','brewStyleNum' => '05','brewStyle' => 'Australian Sparkling Ale [BJCP 12B]','brewStyleCategory' => 'Pale Ale','brewStyleVersion' => 'AABC','brewStyleOG' => '1.038','brewStyleOGMax' => '1.05','brewStyleFG' => '1.004','brewStyleFGMax' => '1.006','brewStyleABV' => '4.5','brewStyleABVMax' => '6','brewStyleIBU' => '20','brewStyleIBUMax' => '35','brewStyleSRM' => '4','brewStyleSRMMax' => '7','brewStyleType' => '1','brewStyleInfo' => 'Smooth and balanced, all components merge together with similar intensities. Moderate flavors showcasing Australian ingredients. Large flavor dimension. Very drinkable, suited to a hot climate. Relies on yeast character. ','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, pale-color, top-fermented, pacific, traditional-style, pale-ale-family, bitter','brewStyleComEx' => 'Coopers Sparkling Ale, Coopers Original Pale Ale.','brewStyleEntry' => NULL),
