  array('brewStyleGroup' => '06','brewStyleNum' => '03','brewStyle' => 'Best Bitter [BJCP 11B]','brewStyleCategory' => 'Bitter Ale','brewStyleVersion' => 'AABC','brewStyleOG' => '1.04','brewStyleOGMax' => '1.048','brewStyleFG' => '1.008','brewStyleFGMax' => '1.012','brewStyleABV' => '3.8','brewStyleABVMax' => '4.6','brewStyleIBU' => '25','brewStyleIBUMax' => '40','brewStyleSRM' => '8','brewStyleSRMMax' => '16','brewStyleType' => '1','brewStyleInfo' => 'A flavorful, yet refreshing, session beer. Some examples can be more malt balanced, but this should not override the overall bitter impression. Drinkability is a critical component of the style. ','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleLink' => 'http://bjcp.org/stylecenter.php','brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, amber-color, top-fermented, british-isles, traditional-style, amber-ale-family, bitter','brewStyleComEx' => 'Timothy Taylor Landlord, Fullers London Pride, Coniston Bluebird Bitter, Adnams SSB, Youngs Special, Shepherd Neame Masterbrew Bitter.','brewStyleEntry' => NULL),
