  array('brewStyleGroup' => '27','brewStyleNum' => 'A4','brewStyle' => 'London Brown Ale','brewStyleCategory' => 'Historical Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.033','brewStyleOGMax' => '1.038','brewStyleFG' => '1.012','brewStyleFGMax' => '1.015','brewStyleABV' => '2.8','brewStyleABVMax' => '3.6','brewStyleIBU' => '15','brewStyleIBUMax' => '20','brewStyleSRM' => '22','brewStyleSRMMax' => '35','brewStyleType' => '1','brewStyleInfo' => 'A luscious, sweet, malty, low-alcohol dark brown ale, with caramel and toffee malt complexity and a sweet-tasting finish.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'session-strength, dark-color, top-fermented, britishisles, historical-style, brown-ale-family, malty, sweet','brewStyleComEx' => 'Harveys Bloomsbury Brown Ale, Mann’s Brown Ale','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '27','brewStyleNum' => 'A5','brewStyle' => 'Piwo Grodziskie','brewStyleCategory' => 'Historical Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.028','brewStyleOGMax' => '1.032','brewStyleFG' => '1.01','brewStyleFGMax' => '1.015','brewStyleABV' => '4.5','brewStyleABVMax' => '6','brewStyleIBU' => '25','brewStyleIBUMax' => '40','brewStyleSRM' => '3','brewStyleSRMMax' => '6','brewStyleType' => '1','brewStyleInfo' => 'A low-gravity, bitter, oak-smoked historical central European wheat beer with a clean fermentation profile and no sourness. Highly carbonated, dry, crisp, and refreshing.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, pale-color, bottom-fermented,lagered, north-america, historical-style, pilsner-family, bitter, hoppy','brewStyleComEx' => 'Live Oak Grodziskie','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '27','brewStyleNum' => 'A6','brewStyle' => 'Pre-Prohibition Lager','brewStyleCategory' => 'Historical Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.044','brewStyleOGMax' => '1.06','brewStyleFG' => '1.01','brewStyleFGMax' => '1.015','brewStyleABV' => '4.5','brewStyleABVMax' => '6','brewStyleIBU' => '25','brewStyleIBUMax' => '40','brewStyleSRM' => '3','brewStyleSRMMax' => '6','brewStyleType' => '1','brewStyleInfo' => 'A bitter and hoppy pale American adjunct lager, often with a robust, corny flavor profile, although more crisp and neutral-tasting versions exist.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, pale-color, bottom-fermented, lagered, north-america, historical-style, pilsner-family, bitter, hoppy','brewStyleComEx' => 'Capital Supper Club, Coors Batch 19, Little Harpeth Chicken Scratch, Schell Deer Brand, Urban Chestnut Forest Park Pilsner','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '27','brewStyleNum' => 'A7','brewStyle' => 'Pre-Prohibition Porter','brewStyleCategory' => 'Historical Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.046','brewStyleOGMax' => '1.06','brewStyleFG' => '1.01','brewStyleFGMax' => '1.016','brewStyleABV' => '4.5','brewStyleABVMax' => '6','brewStyleIBU' => '20','brewStyleIBUMax' => '30','brewStyleSRM' => '20','brewStyleSRMMax' => '30','brewStyleType' => '1','brewStyleInfo' => 'A historical American adaptation of English Porter by German immigrants using American ingredients, including adjuncts.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, dark-color, any-fermentation, northamerica, historical-style, porter-family, malty','brewStyleComEx' => 'Stegmaier Porter, Yuengling Porter','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '27','brewStyleNum' => 'A8','brewStyle' => 'Roggenbier','brewStyleCategory' => 'Historical Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.046','brewStyleOGMax' => '1.056','brewStyleFG' => '1.01','brewStyleFGMax' => '1.014','brewStyleABV' => '4.5','brewStyleABVMax' => '6','brewStyleIBU' => '10','brewStyleIBUMax' => '20','brewStyleSRM' => '14','brewStyleSRMMax' => '19','brewStyleType' => '1','brewStyleInfo' => 'A Dunkles Weissbier made with rye rather than wheat, but with a greater body and light finishing hops. The rye gives a bready and peppery flavor, a creamy body, and a dry, grainy finish that blends with the distinctive banana-and-clove weizen yeast character.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'standard-strength, amber-color, top-fermenting, central-europe, historical-style, wheat-beer-family','brewStyleComEx' => 'Thurn und Taxis Roggen','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '27','brewStyleNum' => 'A9','brewStyle' => 'Sahti','brewStyleCategory' => 'Historical Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.076','brewStyleOGMax' => '1.12','brewStyleFG' => '1.016','brewStyleFGMax' => '1.038','brewStyleABV' => '7','brewStyleABVMax' => '11','brewStyleIBU' => '0','brewStyleIBUMax' => '15','brewStyleSRM' => '4','brewStyleSRMMax' => '22','brewStyleType' => '1','brewStyleInfo' => 'A sweet, heavy, strong traditional Finnish farmhouse beer usually with rye and juniper, and a banana-clove yeast character.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'high-strength, amber-color, top-fermented, centraleurope, historical-style, spice','brewStyleComEx' => 'Now made year-round by several breweries in Finland.','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '28','brewStyleNum' => 'A','brewStyle' => 'Brett Beer','brewStyleCategory' => 'American Wild Ale','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'Most often drier and fruitier than the base style suggests. Fruity or funky notes range from low to high, depending on the age of the beer and strains of Brett used. May possess a light non-lactic acidity. Intended for beer with or without oak aging that has been fermented with Sacch and Brett, or with Brett only.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'wild-fermentation, north-america, craft-style, specialty-beer','brewStyleComEx' => 'Boulevard Saison Brett, Hill Farmstead Arthur, Logsdon Seizoen Bretta, Lost Abbey Brett Devo, Russian River Sanctification, The Bruery Saison Rue','brewStyleEntry' => 'The entrant MUST specify either a Base Style, or provide a description of the ingredients, specs, or desired character. The entrant MAY specify the strains of Brett used.'),
  array('brewStyleGroup' => '28','brewStyleNum' => 'B','brewStyle' => 'Mixed Fermentation Sour Beer','brewStyleCategory' => 'American Wild Ale','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A sour and funky version of a base style of beer. Intended for beer fermented with any combination of Sacch, Lacto, Pedio, and Brett (or additional yeast or bacteria), with or without oak aging (except if the beer fits instead in 28A or 28D).','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'wild-fermentation, north-america, craft-style, specialty-beer, sour','brewStyleComEx' => 'Boulevard Love Child, Jester King Le Petit Prince, Jolly Pumpkin Oro de Calabaza, Lost Abbey Ghosts in the Forest, New Belgium Le Terroir, Russian River Temptation','brewStyleEntry' => 'The entrant MUST specify a description of the beer, identifying yeast or bacteria used and either a Base Style, or the ingredients, specs, or target character of the beer.'),
  array('brewStyleGroup' => '28','brewStyleNum' => 'C','brewStyle' => 'Wild Specialty Beer','brewStyleCategory' => 'American Wild Ale','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'An American Wild Ale with fruit, herbs, spices, or other Specialty-Type Ingredients. Intended for variations of a Base Style beer from style 28A, 28B, or 28D. These variations may include the addition of one or more Specialty-Type Ingredients; aging in non-traditional wood varieties that impart a significant and identifiable wood character (e.g., Spanish Cedar, Amburana); or aging in barrels previously containing another alcohol (e.g., spirits, wine, cider).','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'wild-fermentation, north-america, craft-style, specialty-beer, sour, fruit','brewStyleComEx' => 'Cascade Bourbonic Plague, Jester King Atrial Rubicite, New Belgium Dominga Mimosa Sour, New Glarus Wisconsin Belgian Red, Russian River Supplication, The Lost Abbey Cuvee de Tomme','brewStyleEntry' => 'Entrant MUST specify any Specialty-Type Ingredient (e.g., fruit, spice, herb, or wood) used. Entrant MUST specify either a description of the beer, identifying yeast or bacteria used and either a Base Style, or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '28','brewStyleNum' => 'D','brewStyle' => 'Straight Sour Beer','brewStyleCategory' => 'American Wild Ale','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.048','brewStyleOGMax' => '1.065','brewStyleFG' => '1.006','brewStyleFGMax' => '1.013','brewStyleABV' => '4.5','brewStyleABVMax' => '7','brewStyleIBU' => '3','brewStyleIBUMax' => '8','brewStyleSRM' => '2','brewStyleSRMMax' => '3','brewStyleType' => '1','brewStyleInfo' => 'A pale, refreshing, sour beer with a clean lactic sourness. A gentle, pale malt flavor supports the lemony sourness with moderate fruity esters. Intended for beers fermented with Sacch and Lacto, with or without oak aging, produced using any technique (e.g., traditional co-fermentation, quick kettle souring).','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'pale-color, top-fermented, sour','brewStyleComEx' => 'Rarely found, as this style is typically the base for other Specialty-Type Beers.','brewStyleEntry' => NULL),
  array('brewStyleGroup' => '29','brewStyleNum' => 'A','brewStyle' => 'Fruit Beer','brewStyleCategory' => 'Fruit Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A pleasant integration of fruit with beer, but still recognizable as beer. The fruit character should be evident but in balance with the beer, not so forward as to suggest an artificial product.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, fruit','brewStyleComEx' => '21st Amendment Watermelon Wheat, Anderson Valley Blood Orange Gose, Avery Liliko’i Kepolo, Ballast Point Grapefruit Sculpin, Bell’s Cherry Stout, Founders Rübæus','brewStyleEntry' => 'The entrant MUST specify the type(s) of fruit used. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.  Fruit Beers based on a Classic Style should be entered in this style, except Lambic '),
  array('brewStyleGroup' => '29','brewStyleNum' => 'B','brewStyle' => 'Fruit and Spice Beer','brewStyleCategory' => 'Fruit Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A tasteful union of fruit, spice, and beer, but still recognizable as beer. The fruit and spice character should each be evident but in balance with the beer, not so forward as to suggest an artificial product. Use the definitions of Fruit in the preamble to Category 29 and Spice in the preamble to Category 30; any combination of ingredients valid in Styles 29A and 30A are allowable in this category. For this style, the word ','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, fruit, spice','brewStyleComEx' => 'Cigar City Margarita Gose, Firestone Walker Chocolate Cherry Stout, Golden Road Spicy Mango Cart, Kona Island Colada Cream Ale, New Glarus Blueberry Cocoa Stout, Sun King Orange Vanilla Sunlight','brewStyleEntry' => 'The entrant MUST specify the type of fruit, and the type of SHV used; individual SHV ingredients do not need to be specified if a well-known blend of spices is used (e.g., apple pie spice). Entrant MUST specify a description of the beer, either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '29','brewStyleNum' => 'C','brewStyle' => 'Specialty Fruit Beer','brewStyleCategory' => 'Fruit Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A appealing combination of fruit, sugar, and beer, but still recognizable as a beer. The fruit and sugar character should both be evident but in balance with the beer, not so forward as to suggest an artificial product. A Specialty Fruit Beer is a Fruit Beer with some additional ingredients, such as fermentable sugars (e.g., honey, brown sugar, invert sugar), sweeteners (e.g., lactose), adjuncts, alternative grains, or other special ingredients added, or some additional process applied. A Specialty Fruit Beer can use any style within the Fruit Beer category as a base style (currently, 29A, 29B, or 29D).','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, fruit','brewStyleComEx' => 'The Bruery Goses are Red, New Planet Raspberry Ale, Tired Hands Strawberry Milkshake IPA, WeldWerks Piña Colada IPA','brewStyleEntry' => 'The entrant MUST specify the type of fruit used. The entrant MUST specify the type of additional ingredient (per the introduction) or special process employed. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '29','brewStyleNum' => 'D','brewStyle' => 'Grape Ale','brewStyleCategory' => 'Fruit Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.059','brewStyleOGMax' => '1.075','brewStyleFG' => '1.004','brewStyleFGMax' => '1.013','brewStyleABV' => '6','brewStyleABVMax' => '8.5','brewStyleIBU' => '10','brewStyleIBUMax' => '30','brewStyleSRM' => '4','brewStyleSRMMax' => '8','brewStyleType' => '1','brewStyleInfo' => 'Combines the profile of a sparkling wine and a relatively neutral base beer allowing the aromatic qualities of the grape to blend pleasantly with hop and yeast aromatics. Can be in a range from refreshing to complex.','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, fruit','brewStyleComEx' => 'Montegioco Open Mind, Birrificio del Forte Il Tralcio, Luppolajo Mons Rubus, Firestone Walker Feral Vinifera, pFriem Family Brewers Druif, 4 Árvores Abbondanza','brewStyleEntry' => 'The entrant MUST specify the type of grape used. The entrant MAY provide additional information about the base style or characteristic ingredients.'),
  array('brewStyleGroup' => '30','brewStyleNum' => 'A','brewStyle' => 'Spice, Herb, or Vegetable Beer','brewStyleCategory' => 'Spiced Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'An appealing fusion of spices, herbs, or vegetables (SHVs) and beer, but still recognizable as beer. The SHV character should be evident but in balance with the beer, not so forward as to suggest an artificial product. Often called Spice Beer, regardless of whether spices, herbs, or vegetables are used.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, spice','brewStyleComEx' => 'Alesmith Speedway Stout, Elysian Avatar Jasmine IPA, Founders Breakfast Stout, Rogue Yellow Snow Pilsner, Traquair Jacobite Ale, Young’s Double Chocolate Stout','brewStyleEntry' => 'The entrant MUST specify the type of spices, herbs, or vegetables used, but individual ingredients do not need to be specified if a well-known spice blend is used (e.g., apple pie spice, curry powder, chili powder). Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '30','brewStyleNum' => 'B','brewStyle' => 'Autumn Seasonal Beer','brewStyleCategory' => 'Spiced Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A malty, spiced beer that often has a moderately rich body and slightly warming finish suggesting a good accompaniment for the cool fall season, and often evocative of harvest or Thanksgiving traditions. Autumn Seasonal Beers are beers that suggest cool weather and the autumn harvest season, and may include pumpkins, gourds, or other squashes, and associated spices.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, spice','brewStyleComEx' => 'Dogfish Head Punkin Ale, Elysian Punkuccino, Rogue Pumpkin Patch Ale, Schlafly Pumpkin Ale, UFO Pumpkin, Weyerbacher Imperial Pumpkin','brewStyleEntry' => 'The entrant MUST specify the type of spices, herbs, or vegetables used; individual ingredients do not need to be specified if a well-known blend of spices is used (e.g., pumpkin pie spice). Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '30','brewStyleNum' => 'C','brewStyle' => 'Winter Seasonal Beer','brewStyleCategory' => 'Spiced Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A stronger, darker, spiced beer that often has a rich body and warming finish suggesting a good accompaniment for the cold winter season. Winter Seasonal Beers are beers that suggest cold weather and the Christmas holiday season, and may include holiday spices, specialty sugars, and other products that are reminiscent of the festive season. ','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, spice','brewStyleComEx' => 'Anchor Christmas Ale, Great Lakes Christmas Ale, Harpoon Winter Warmer, Rogue Santa’s Private Reserve, Schlafly Christmas Ale, Troeg’s The Mad Elf','brewStyleEntry' => 'The entrant MUST specify the type of spices, sugars, fruits, or additional fermentables used; individual ingredients do not need to be specified if a well-known blend of spices is used (e.g., mulling spice). Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '30','brewStyleNum' => 'D','brewStyle' => 'Specialty Spice Beer','brewStyleCategory' => 'Spiced Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'An appealing combination of spices, herbs, or vegetables (SHVs), sugars, and beer, but still recognizable as beer. The SHV and sugar character should both be evident but in balance with the beer, not so forward as to suggest an artificial product. A Specialty Spice Beer is a 30A Spice, Herb, or Vegetable (SHV) Beer with some additional ingredients, such as fermentable sugars (e.g., honey, brown sugar, invert sugar, maple syrup), sweeteners (e.g., lactose), adjuncts, alternative grains, or other special ingredients added, or some additional process applied. 30B Autumn and 30C Winter Seasonal Beers already allow additional ingredients, and should not be used as a base in this style.','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, spice','brewStyleComEx' => 'New Belgium Honey Orange Tripel, Westbrook It’s Tiki Time','brewStyleEntry' => 'The entrant MUST specify the type of SHVs used, but individual ingredients do not need to be specified if a well-known spice blend is used (e.g., apple pie spice, curry powder, chili powder). The entrant MUST specify the type of additional ingredient (per the introduction) or special process employed. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '31','brewStyleNum' => 'A','brewStyle' => 'Alternative Grain Beer','brewStyleCategory' => 'Alternative Fermentables Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A base beer enhanced by or featuring the character of additional grains. The specific character depends greatly on the added grains. An Alternative Grain Beer is a standard beer (Classic Style or not) with additional or non-standard brewing grains (e.g., rye, oats, buckwheat, spelt, millet, sorghum, rice) added or used exclusively. Gluten-free (GF) beers made from completely gluten-free ingredients may be entered here, while GF beers using process-based gluten removal should be entered in their respective base style categories. ','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer','brewStyleComEx' => 'Blue/Point Rastafarye Ale, Green’s India Pale Ale, Lakefront New Grist, New Planet Pale Ale, Rogue Morimoto Soba Ale, Voodoo Swimming Jeans','brewStyleEntry' => 'he entrant MUST specify the type of alternative grain used. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '31','brewStyleNum' => 'B','brewStyle' => 'Alternative Sugar Beer','brewStyleCategory' => 'Alternative Fermentables Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A tasteful integration of sugar and beer, but still recognizable as beer. The sugar character should both be evident and in balance with the beer, not so forward as to suggest an artificial product. An Alternative Sugar Beer is a standard beer (Classic Style or not) with added sweeteners, including fermentable sugars (e.g., honey, brown sugar, invert sugar, molasses, treacle, maple syrup, sorghum), unfermentable sugars (e.g., lactose), sugar alcohols (e.g., sorbitol), and any other sweetener (natural or artificial) that affects the flavor profile. The beers may or may not have any residual sweetness; it depends on the type of sugar, but flavor contributions are expected.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer','brewStyleComEx' => 'Bell’s Hopslam, Cervejaria Colorado Appia, Fifth Hammer Break of Jawn, Groundswell Piloncillo, Long Trail Harvest, New Glarus Cabin Fever','brewStyleEntry' => 'The entrant MUST specify the type of sugar used. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '32','brewStyleNum' => 'A','brewStyle' => 'Classic Style Smoked Beer','brewStyleCategory' => 'Smoked Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A well-balanced fusion of the malt and hops of the base beer style with a pleasant and agreeable smoke character. Intended for smoked versions of Classic Style beers, except if the Classic Style beer has smoke as an inherent part of its definition (of course, that beer should be entered in its base style, such as Rauchbier).','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, smoke','brewStyleComEx' => 'Alaskan Smoked Porter, Schlenkerla Oak Smoke Doppelbock, Schlenkerla Rauchbier Weizen, Schlenkerla Rauchbier Ur-Bock, O’Fallon Smoked Porter, Spezial Rauchbier Lagerbier','brewStyleEntry' => 'The entrant MUST specify a Base Style. The entrant MUST specify the type of wood or smoke if a varietal smoke character is noticeable.'),
  array('brewStyleGroup' => '32','brewStyleNum' => 'B','brewStyle' => 'Specialty Smoked Beer','brewStyleCategory' => 'Smoked Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A well-balanced fusion of the malt and hops of the base specialty beer style with a pleasant and agreeable smoke character. A Specialty Smoked Beer is either a smoked beer based on something other than a Classic Style (a Specialty-Type style, or a broad style family such as Porter rather than a specific style), OR any type of smoked beer with additional specialty ingredients (fruits, vegetables, spices) or processes employed that transform the beer into something more unique.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, smoke','brewStyleComEx' => 'Fat Head’s Up in Smoke, Ommegang Bourbon Barrel Vanilla Smoked Porter','brewStyleEntry' => 'The entrant MUST specify the type of wood or smoke if a varietal smoke character is noticeable. The entrant MUST specify the additional ingredients or processes that make this a specialty smoked beer. Entrant MUST specify a description of the beer, identifying either a base style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '33','brewStyleNum' => 'A','brewStyle' => 'Wood-Aged Beer','brewStyleCategory' => 'Wood Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'A pleasant enhancement of the base beer style with the characteristics from aging in contact with wood. The best examples will be smooth, flavorful, well-balanced, and well-aged. This style is intended for beer aged in wood without added alcohol character from previous use of the barrel. Bourbon-barrel or other beers with an added alcohol character should be entered as 33B Specialty Wood-Aged Beer. This category should not be used for base styles where wood-aging is a fundamental requirement for the style (e.g., Flanders Red, Lambic). Beers made using either limited wood aging or products that only provide a subtle background character may be entered in the base beer style categories as long as the wood character isn','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, wood','brewStyleComEx' => 'Bush Prestige, Cigar City Spanish Cedar Jai Alai, Firestone Walker Double Barrel Ale, Midnight Sun Arctic Devil, Petrus Aged Pale, Samuel Smith Yorkshire Stingo','brewStyleEntry' => 'The entrant MUST specify the type of wood used and the toast or char level (if used). If an unusual varietal wood is used, the entrant MUST supply a brief description of the sensory aspects the wood adds to beer. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '33','brewStyleNum' => 'B','brewStyle' => 'Specialty Wood-Aged Beer','brewStyleCategory' => 'Wood Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'An elevation of the base beer style with characteristics from aging in contact with wood, including alcoholic products previously in contact with the wood. The best examples will be smooth, flavorful, well-balanced, and well-aged. This style is intended for beer aged in wood with added alcohol character from previous use of the barrel. Bourbon-barrel or other similar beers should be entered here.   ','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer, wood','brewStyleComEx' => 'AleSmith Barrel-Aged Old Numbskull, Founders Kentucky Breakfast Stout, Firestone Walker Parabola, Goose Island Bourbon County Stout, Great Divide Barrel Aged Yeti, The Lost Abbey Angel’s Share Ale','brewStyleEntry' => 'The entrant MUST specify the additional alcohol character, with information about the barrel if relevant to the finished flavor profile. If an unusual wood or ingredient has been used, the entrant MUST supply a brief description of the sensory aspects the ingredients add to the beer. Entrant MUST specify a description of the beer, identifying either a Base Style or the ingredients, specs, or target character of the beer. A general description of the special nature of the beer can cover all the required items.'),
  array('brewStyleGroup' => '34','brewStyleNum' => 'A','brewStyle' => 'Commercial Specialty Beer','brewStyleCategory' => 'Specialty Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'This style is intended for reproductions or interpretations of specific commercial beers that don’t fit within defined styles. Beers entered here do not need to be exact copies. The beer should be judged as to how well it fits the broader style represented by the example beer, not how well it is an exact copy of a specific commercial product. If a Commercial Specialty Beer fits another defined style, do not enter it here.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer','brewStyleComEx' => 'Orval, La Chouffe','brewStyleEntry' => 'The entrant MUST specify the name of the commercial beer, specifications (vital statistics) for the beer, and either a brief sensory description or a list of ingredients used in making the beer. Without this information, judges who are unfamiliar with the beer will have no basis for comparison.'),
  array('brewStyleGroup' => '34','brewStyleNum' => 'B','brewStyle' => 'Mixed-Style Beer','brewStyleCategory' => 'Specialty Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'Based on the declared Base Styles, methods, and ingredients. As with all Specialty-Type Beers, the resulting combination of beer styles needs to be harmonious and balanced, and be pleasant to drink. This style is intended for beers in Existing Styles (previously-defined Classic Style beers or Specialty-Type Beers) that are either: 1) A combination of Existing Styles that are not defined previously in the guidelines, including combination of Specialty-Type Beers not otherwise allowable elsewhere; 2) A variation of an Existing Style using a non-traditional method or process (e.g., dry-hopping, ‘eis’-ing, stein bier) for that style. A variation of an Existing Style using a non-traditional method or process (e.g., dry-hopping, ‘eis’-ing, stein bier) for that style; 3) A variation of an Existing Style using a non-traditional ingredient (e.g., yeast with a non-traditional profile, hops with a different character than described in the Base Style); 4) Out-of-spec variations of an Existing Style (e.g., ‘imperial’ versions, ‘session’ versions, overly-sweet versions, etc.). This style is intended for beers that can’t be entered in previously-listed styles first, including (and especially) the declared Base Style of beer. However, if the unusual method, process, or ingredient results in a beer that now fits within another defined style, the beer should be entered there. Note that some styles already allow for different strengths (e.g., IPAs, Saisons), so those variations should be entered as the appropriate Base Style.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer','brewStyleComEx' => 'Birrificio Italiano Tipopils, Firestone Walker Pivo Pils, Jack’s Abby Hoponius Union, Ommegang Helles Superior','brewStyleEntry' => ' The entrant MUST specify the Base Style or Styles being used, and any special ingredients, processes, or variations involved. The entrant MAY provide an additional description of the sensory profile of the beer or the vital statistics of the resulting beer.'),
  array('brewStyleGroup' => '34','brewStyleNum' => 'C','brewStyle' => 'Experimental Beer','brewStyleCategory' => 'Specialty Beer','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '','brewStyleOGMax' => '','brewStyleFG' => '','brewStyleFGMax' => '','brewStyleABV' => '','brewStyleABVMax' => '','brewStyleIBU' => '','brewStyleIBUMax' => '','brewStyleSRM' => '','brewStyleSRMMax' => '','brewStyleType' => '1','brewStyleInfo' => 'This is explicitly a catch-all category for any beer that does not fit into an Existing Style description. No beer is ever “out of style” in this style, unless it can be entered in another beer style first. This is the last resort for any beer entered into a competition. With the broad definition for previous styles, this style should be rarely used.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'specialty-beer','brewStyleComEx' => NULL,'brewStyleEntry' => 'The entrant MUST specify the special nature of the experimental beer, including the special ingredients or processes that make it not fit elsewhere in the guidelines. The entrant MUST provide vital statistics for the beer, and either a brief sensory description or a list of ingredients used in making the beer. Without this information, judges will have no basis for evaluation.'),
  array('brewStyleGroup' => 'LS','brewStyleNum' => 'X1','brewStyle' => 'Dorada Pampeana','brewStyleCategory' => 'Local Styles','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.042','brewStyleOGMax' => '1.054','brewStyleFG' => '1.009','brewStyleFGMax' => '1.013','brewStyleABV' => '4.3','brewStyleABVMax' => '5.5','brewStyleIBU' => '15','brewStyleIBUMax' => '22','brewStyleSRM' => '3','brewStyleSRMMax' => '5','brewStyleType' => '1','brewStyleInfo' => 'At the beginning Argentine homebrewers were very limited: there weren','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => '','brewStyleComEx' => NULL,'brewStyleEntry' => ''),
  array('brewStyleGroup' => 'LS','brewStyleNum' => 'X2','brewStyle' => 'IPA Argenta','brewStyleCategory' => 'Local Styles','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.055','brewStyleOGMax' => '1.065','brewStyleFG' => '1.008','brewStyleFGMax' => '1.015','brewStyleABV' => '5','brewStyleABVMax' => '6.5','brewStyleIBU' => '35','brewStyleIBUMax' => '60','brewStyleSRM' => '6','brewStyleSRMMax' => '15','brewStyleType' => '1','brewStyleInfo' => 'A decidedly hoppy and bitter, refreshing and moderately strong Argentine pale ale. The clue is drinkability without harshness and best balance. An Argentine version of the historical English style, developed in 2013 from Somos Cerveceros Association meetings, when its distinctive characteristics were defined. Different from an American IPA in that it is brewed with wheat and using Argentine hops, with its unique flavor and aroma characteristic. Based on a citrus (from Argentine hop) and wheat pairing idea, like in a Witbier. Low amounts of wheat are similar to a K','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => '','brewStyleComEx' => 'Antares Ipa Argenta, Kerze Ipa Argenta','brewStyleEntry' => NULL),
  array('brewStyleGroup' => 'LS','brewStyleNum' => 'X3','brewStyle' => 'Italian Grape Ale','brewStyleCategory' => 'Local Styles','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.043','brewStyleOGMax' => '1.09','brewStyleFG' => '1.007','brewStyleFGMax' => '1.015','brewStyleABV' => '4.8','brewStyleABVMax' => '10','brewStyleIBU' => '10','brewStyleIBUMax' => '30','brewStyleSRM' => '5','brewStyleSRMMax' => '30','brewStyleType' => '1','brewStyleInfo' => 'A sometimes refreshing, sometimes more complex Italian ale characterized by different varieties of grapes. For uses outside Italy, see 29D Grape Ale.','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => '','brewStyleComEx' => 'Montegioco Open Mind, Birrificio Barley BB5-10, Birrificio del Forte Il Tralcio, Viess Beer al mosto di gewurtztraminer, CRAK IGA Cabernet, Birrificio Apuano Ninkasi, Luppolajo Mons Rubus','brewStyleEntry' => 'The entrant MUST specify the type of grape used. The entrant MAY provide additional information about the base style or characteristic ingredients.'),
  array('brewStyleGroup' => 'LS','brewStyleNum' => 'X4','brewStyle' => 'Catharina Sour','brewStyleCategory' => 'Local Styles','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.039','brewStyleOGMax' => '1.048','brewStyleFG' => '1.002','brewStyleFGMax' => '1.008','brewStyleABV' => '4','brewStyleABVMax' => '5.5','brewStyleIBU' => '2','brewStyleIBUMax' => '68','brewStyleSRM' => '2','brewStyleSRMMax' => '7','brewStyleType' => '1','brewStyleInfo' => 'A refreshing fruited sour wheat beer with a vibrant fruit character and a clean lactic acidity. The restrained alcohol, light body, elevated carbonation, and lack of perceived bitterness allows the fresh fruit to be highlighted. The fruit is often, but not always, tropical in nature.','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '1','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'craft-style, fruit, sour, specialty-beer','brewStyleComEx' => 'Armada Daenerys, Blumenau Catharina Sour Pêssego, Istepô Goiabêra, Itajahy Catharina Araçá Sour, Liffey Coroa Real, UNIKA Tangerina Clemenules','brewStyleEntry' => 'Entrant MUST specify the types of fresh fruit(s) used.'),
  array('brewStyleGroup' => 'LS','brewStyleNum' => 'X5','brewStyle' => 'New Zealand Pilsner','brewStyleCategory' => 'Local Styles','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.044','brewStyleOGMax' => '1.046','brewStyleFG' => '1.009','brewStyleFGMax' => '1.014','brewStyleABV' => '4.5','brewStyleABVMax' => '5.8','brewStyleIBU' => '25','brewStyleIBUMax' => '45','brewStyleSRM' => '2','brewStyleSRMMax' => '7','brewStyleType' => '1','brewStyleInfo' => 'A pale, dry, golden-colored, cleanly-fermented beer showcasing the characteristic tropical, citrusy, fruity, grassy New Zealand-type hops. Medium body, soft mouthfeel, and smooth palate and finish, with a neutral to bready malt base provide the support for this very drinkable, refreshing, hop-forward beer.','brewStyleLink' => NULL,'brewStyleActive' => 'N','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '0','brewStyleSweet' => '0','brewStyleTags' => 'bitter, pale-color, standard-strength, bottom-fermented, hoppy, pilsner-family, lagered, craft-style, pacific','brewStyleComEx' => 'Croucher New Zealand Pilsner, Emerson’s Pilsner, Liberty Halo Pilsner, Panhead Port Road Pilsner, Sawmill Pilsner, Tuatara Mot Eureka','brewStyleEntry' => NULL),
  array('brewStyleGroup' => 'C1','brewStyleNum' => 'A','brewStyle' => 'New World Cider','brewStyleCategory' => 'Standard Cider and Perry','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.045','brewStyleOGMax' => '1.065','brewStyleFG' => '0.995','brewStyleFGMax' => '1.02','brewStyleABV' => '5','brewStyleABVMax' => '8','brewStyleIBU' => 'N/A','brewStyleIBUMax' => 'N/A','brewStyleSRM' => 'N/A','brewStyleSRMMax' => 'N/A','brewStyleType' => '2','brewStyleInfo' => 'A refreshing drink of some substance - not bland or watery. Sweet ciders MUST not be cloying. Dry ciders MUST not be too austere. ','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '1','brewStyleSweet' => '1','brewStyleTags' => '','brewStyleComEx' => '[US] Uncle John&rsquo;s Fruit House Winery Apple Hard Cider, Tandem Ciders Pretty Penny (MI), Bellwether Spyglass (NY), West County Pippin (MA), White Winter Hard Apple Cider (WI), Wandering Aengus Ciderworks Bloom (OR), &Aelig;ppeltreow Appely Brut and Doux (WI).','brewStyleEntry' => NULL),
