  array('brewStyleGroup' => 'C1','brewStyleNum' => 'C','brewStyle' => 'French Cider','brewStyleCategory' => 'Standard Cider and Perry','brewStyleVersion' => 'BJCP2021','brewStyleOG' => '1.05','brewStyleOGMax' => '1.065','brewStyleFG' => '1.01','brewStyleFGMax' => '1.02','brewStyleABV' => '3','brewStyleABVMax' => '6','brewStyleIBU' => 'N/A','brewStyleIBUMax' => 'N/A','brewStyleSRM' => 'N/A','brewStyleSRMMax' => 'N/A','brewStyleType' => '2','brewStyleInfo' => 'Medium to sweet, full-bodied, rich.','brewStyleLink' => NULL,'brewStyleActive' => 'Y','brewStyleOwn' => 'bcoe','brewStyleReqSpec' => '0','brewStyleStrength' => '0','brewStyleCarb' => '1','brewStyleSweet' => '1','brewStyleTags' => '','brewStyleComEx' => '[US] West County Reine de Pomme (MA), [France] Eric Bordelet (various), Etienne Dupont, Etienne Dupont Organic, Bellot.','brewStyleEntry' => NULL),
